+<?php

+namespace App\Http\Controllers\Main;
+
+use App\Http\Controllers\Controller;
+use App\Models\Kelompok;
+use App\Models\PembelianSarana;
+use App\Models\User;
+use Illuminate\Http\Request;
+
+class PembelianSaranaController extends Controller
+{
+    /**
+     * Display a listing of the resource.
+     */
+    public function index()
+    {
+        if ($user = auth()->user()->jab_id == 4) {
+            $user = auth()->user()->id;
+            $data['title'] = 'Data pengajuan pembelian sarana';
+            $user = PembelianSarana::with('users', 'kelompok')
+                ->where('user_id', $user)
+                ->get();
+            $data['result'] = $user->sortDesc();
+            return view('main.pembelian_sarana.index', $data);
+        } else {
+            $data['title'] = 'Data pengajuan pembelian sarana';
+            $user = PembelianSarana::with('users', 'kelompok')
+                ->get();
+            $data['result'] = $user->sortDesc();
+            return view('main.pembelian_sarana.index', $data);
+        }
+    }
+
+    /**
+     * Show the form for creating a new resource.
+     */
+    public function create()
+    {
+        $data['user'] = User::all();
+        $data['kelompok'] = Kelompok::all();
+        $data['pembelian_sarana'] = PembelianSarana::all();
+        $data['title'] = "Tambah Data Pembelian Sarana";
+        return view("main.pembelian_sarana.create", $data);
+    }
+
+    /**
+     * Store a newly created resource in storage.
+     */
+    public function store(Request $request)
+    {
+        $request->validate([
+            'file1' => 'required|mimes:jpg,png,pdf|max:5048',
+            'user_id' => 'required',
+            'kelompok_id' => 'required',
+            'sarana' => 'required',
+            'spesifikasi' => 'required',
+            'harga' => 'required',
+            'keterangan' => 'required',
+            'jumlah' => 'required',
+            'tanggal' => 'required',
+            'status' => 'required',
+        ]);
+        // Proses pengunggahan berkas pertama
+        // Proses pengunggahan berkas kedua
+        $file2 = $request->file('file2');
+        $file2Name = time() . '_' . $file2->getClientOriginalName();
+        $file2->move(public_path('uploads'), $file2Name);
+
+
+        $fileModel = new PembelianSarana();
+
+        $fileModel->user_id = $request->user_id;
+        $fileModel->kelompok_id = $request->kelompok_id;
+        $fileModel->sarana = $request->sarana;
+        $fileModel->spesifikasi = $request->spesifikasi;
+        $fileModel->harga = $request->harga;
+        $fileModel->keterangan = $request->keterangan;
+        $fileModel->jumlah = $request->jumlah;
+        $fileModel->Paraf = $file2Name;
+        $fileModel->tanggal = $request->tanggal;
+        $fileModel->status = $request->status;
+
+        $fileModel->save();
+        return redirect('main/pembelian_sarana')->with('success', 'Data berhasil di simpan.');
+    }
+
+    /**
+     * Display the specified resource.
+     */
+    public function show(string $id)
+    {
+
+        $row = PembelianSarana::findOrFail($id);
+        return view('main.pembelian_sarana.show', [
+            'title' => 'Detail Data Pembelian Sarana',
+            'kelompok_id' => $row->kelompok_id,
+            'sarana' => $row->sarana,
+            'spesifikasi' => $row->spesifikasi,
+            'harga' => $row->harga,
+            'keterangan' => $row->keterangan,
+            'jumlah' => $row->jumlah,
+            'Paraf' => $row->Paraf,
+            'tanggal' => $row->tanggal,
+        ]);
+    }
+
+    /**
+     * Show the form for editing the specified resource.
+     */
+    public function edit(string $id)
+    {
+
+        $row = PembelianSarana::findOrFail($id);
+        $user = User::all();
+        $kelompok = Kelompok::all();
+        return view('main.pembelian_sarana.edit', [
+            'title' => 'Edit Data Sarpras',
+            'user' => $user,
+            'id' => $row->id,
+            'kelompok' => $kelompok,
+            'sarana' => $row->sarana,
+            'spesifikasi' => $row->spesifikasi,
+            'harga' => $row->harga,
+            'keterangan' => $row->keterangan,
+            'jumlah' => $row->jumlah,
+            'tanggal' => $row->tanggal,
+            'status' => $row->status,
+        ]);
+    }
+
+    /**
+     * Update the specified resource in storage.
+     */
+    public function update(Request $request, string $id)
+    {
+        $request->validate([
+            // 'file1' => 'required|mimes:jpg,png|max:5048',
+            // 'file2' => 'required|mimes:jpg,png|max:5048',
+            // 'file3' => 'required|mimes:jpg,png|max:5048',
+            'user_id' => 'required',
+            'kelompok_id' => 'required',
+            'sarana' => 'required',
+            'spesifikasi' => 'required',
+            'harga' => 'required',
+            'keterangan' => 'required',
+            'jumlah' => 'required',
+            'tanggal' => 'required',
+            'status' => 'required',
+        ]);
+        // // Proses pengunggahan berkas pertama
+        // $file1 = $request->file('file1');
+        // $file1Name = time() . '_' . $file1->getClientOriginalName();
+        // $file1->move(public_path('uploads'), $file1Name);
+
+
+        $fileModel = PembelianSarana::find($id);
+        $fileModel->user_id = $request->user_id;
+        $fileModel->kelompok_id = $request->kelompok_id;
+        $fileModel->sarana = $request->sarana;
+        $fileModel->spesifikasi = $request->spesifikasi;
+        $fileModel->harga = $request->harga;
+        $fileModel->keterangan = $request->keterangan;
+        $fileModel->jumlah = $request->jumlah;
+        $fileModel->tanggal = $request->tanggal;
+        $fileModel->status = $request->status;
+
+        $fileModel->save();
+
+        return redirect('main/pembelian_sarana')->with('success', 'Data berhasil diubah');
+    }
+
+    /**
+     * Remove the specified resource from storage.
+     */
+    public function destroy(string $id)
+    {
+
+        $data = PembelianSarana::findOrFail($id);
+        $paraf = "/uploads/" . $data->paraf;
+
+        if (file_exists($paraf)) {
+            @unlink($paraf);
+        }
+        $data->delete();
+        return back()->with('hapus', 'Data sudah di Hapus!');
+    }
+}

